import React, { Component } from 'react';
import InfiniteScroll from 'react-infinite-scroller';
import Row from './Row'

class Table extends Component {
  state = {
    items: [],
  }

  componentDidMount() {
    fetch('http://localhost:3000/items')
      .then((response) => {
        return response.json();
      })
      .then((items) => {
        // debugger
        this.setState({ items: items })
      })
  }

  fetchMoreData = () => {
    setTimeout(() => {
      this.setState({
        items: this.state.items.concat(Array.from({ length: 20 }))
      });
    }, 1500);
  };

  render() {
    return (
      <table>
        <tr>
          <th> Date </th>
          <th> Description </th>
          <th> Price </th>
        </tr>
        <InfiniteScroll
          pageStart={0}
          loadMore={loadFunc}
          hasMore={true || false}
          loader={<div className="loader" key={0}>Loading ...</div>}
          useWindow={false}
        >
          {this.state.items.map((item) => (
            <Row date={item["date"]} description={item["description"]} price={item["price"]} />
          ))}
        </InfiniteScroll>
      </table>
    );
  }

}

export default Table;
