import React, { Component } from 'react';
import Row from './Row'

class Table extends Component {
  state = {
    items: [],
  }

  componentDidMount() {
    fetch('http://localhost:3000/items')
      .then((response) => {
        return response.json();
      })
      .then((items) => {
        // debugger
        this.setState({ items: items })
      })
  }

  render() {
    return (
      <table>
        <tr>
          <th> Date </th>
          <th> Description </th>
          <th> Price </th>
        </tr>

          {this.state.items.map((item) => (
            <Row date={item["date"]} description={item["description"]} price={item["price"]} />
          ))}

      </table>
    );
  }

}

export default Table;
