import React, { Component } from 'react';
import AddSingleItemModal from './modals/AddSingleItemModal';
import AddShoppingTripModal from './modals/AddShoppingTripModal';

class Table extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: []
    }
  }

  componentDidMount() {
    const fetchItems = async () => {
      try {
        const items = await fetch(`http://localhost:3000/items?sort=${this.props.sort}`)
        .then((response) => {
          return response.json();
        });
        this.setState({
          items: items
        });
      } catch(e) {
        console.log(e);
      }
    };
    fetchItems();
  };

  render() {
    if (!this.state.items) {
      return (
        <div class="alert alert-danger" role="alert">
          Something went wrong - please try again
        </div>
      )
    } else {
      return (
        <React.Fragment>
          <AddSingleItemModal items={this.state.items} setItems={this.setState}/>
          <AddShoppingTripModal items={this.state.items} setItems={this.setState}/>
          <div className="table-responsive">
          <table className="table table-fixed">
            <thead className="thead-light">
              <tr>
                <th scope="col" className="col-4"> Date </th>
                <th scope="col" className="col-4"> Description </th>
                <th scope="col" className="col-4"> Price </th>
              </tr>
              </thead>
              <tbody>
                {this.state.items.map(item =>
                  <tr key={item.id}>
                    <td className="col-4"> {item.date} </td>
                    <td className="col-4"> {item.description} </td>
                    <td className="col-4"> ${item.price} </td>
                  </tr>
                )}
              </tbody>
          </table>
          </div>
        </React.Fragment>
      );
    }
  }
}

export default Table;
