import React, { useState, useEffect } from 'react';

const Table = props => {

  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const items = await fetch(`http://localhost:3000/items?sort=${props.sort}`)
        .then((response) => {
          return response.json();
        });
        setItems(items);
      } catch(e) {
        console.log(e);
      }
    };

    fetchItems();
  }, []);



  return (
    <div className="table-responsive">
    <table className="table table-fixed">
      <thead className="thead-light">
        <tr>
          <th scope="col" className="col-4"> Date </th>
          <th scope="col" className="col-4"> Description </th>
          <th scope="col" className="col-4"> Price </th>
        </tr>
        </thead>
        <tbody>
          {items.map(item =>
            <tr key={item.id}>
              <td className="col-4"> {item.date} </td>
              <td className="col-4"> {item.description} </td>
              <td className="col-4"> ${item.price} </td>
            </tr>
          )}
        </tbody>
    </table>
    </div>
  );

}

export default Table;
