import React, { useState, useEffect } from 'react';
import { StatCard, ListCard } from './Cards'

const Dashboard = () => {
  const [stats, setStats] = useState([999]);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const stats = await fetch(`http://localhost:3000/stats`)
        .then((response) => {
          return response.json();
        });
        setStats(stats.data);
      } catch(e) {
        console.log(e);
        return (<div class="alert alert-danger" role="alert">
  A simple danger alertâ€”check it out!
</div>)
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="dashboard-container">
      {stats.map(stat => {
        if (stat.type === "list") {
          return <ListCard title={stat.title} data={stat.data} />
        } else {
          return <StatCard title={stat.title} type={stat.type} data={stat.data} />
        }
      })}
    </div>
  );


}

export default Dashboard;
