import React from 'react';

const Card = (props) => {
  let dataSection;
  switch(props.type) {
    case "list":
      dataSection = <React.Fragment>
        <ul>
          {props.data.map(item => Object.values(item)).map(a => <li>{a[0]} ({a[1]})</li>)
         }
        </ul>
      </React.Fragment>;
      break;
    case "dollar":
      dataSection = <React.Fragment>
        ${props.data}
      </React.Fragment>;
      break;
    case "stat":
      dataSection = <React.Fragment>
        {props.data}
      </React.Fragment>;
      break;
    default:
      return("");
  }

  return (
    <div className="card">
      <h2>{props.title}</h2>
      <h3>{dataSection}</h3>
    </div>
  );

}

export default Card;
